var http=require("http");
var fs=require("fs");
http.createServer(function(request,response){
    var url="."+request.url;
    fs.readFile(url,function(err,data){
        if(err){
            response.write("404");
        }else{
            response.write(data.toString());
        }
        response.end();
    })
}).listen(8080);
